  public static int determineFloor(String s) {
    int floor = 0;
    char[] sArr = s.toCharArray();
    int i = 0;
    for (char c : sArr) {
      if (c == '(') {
        floor++;
      } else if (c == ')') {
        floor--;
      }
      if (floor == -1) {
        System.out.println("Basement Hit!");
        return (i + 1);
      }
      i++;
    }
    return floor;
  }

